import { useState } from "react";
import { useNavigate } from "react-router-dom";
import './login.css';
import { GoArrowLeft } from "react-icons/go";


  const login = () => {
  return (
    <div classname="wrapper">
      <from action= "">
        <div className="back">
          <a href="/"><GoArrowLeft classname="icon"/>Back</a>
        </div>
        <h1 classname='text_sign'>Sign In</h1>
        <p>enter your phone to sign in</p>
        <div>
           <input type="text" placeholder="Your Phone Number" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </div>
        {step === 1 ? (
          <button onClick={sendCode}>Next</button>
        ) : (
          <div>
            <input type="text" placeholder="Enter Access Code" value={code} onChange={(e) => setCode(e.target.value)} />
            <button onClick={verifyCode}>Verify Code</button>
          </div>
        )}
        <p>Passwordless authentication methods</p>
        <p>Don't having account?
        <a href="/signup" className="link_sign_up">  Sign Up</a>
        </p>
      </from>
    </div>
  );

}
