import { useState } from "react";
import { createAccessCode, validateAccessCode } from "../../services/api";
import { useNavigate } from "react-router-dom";
import './login.css';
import { GoArrowLeft } from "react-icons/go";

export default function Login() {
  const [phone, setPhone] = useState("");
  const [code, setCode] = useState("");
  const [step, setStep] = useState(1);
  const navigate = useNavigate();

  const sendCode = async (e) => {
    e.preventDefault();
    await createAccessCode(phone);
    setStep(2);
  };

  const verifyCode = async (e) => {
    e.preventDefault();
    const res = await validateAccessCode(phone, code);
    const role = res.data.role;
    localStorage.setItem("phone", phone);
    localStorage.setItem("role", role);
    if (role === "instructor") navigate("/instructor");
    else navigate("/student");
  };

  return (
    <div className="wrapper">
      <div className="logincontainer">
      <form>
        <div className="back">
          <a href="/" className="back-link"><GoArrowLeft className="icon" />Back</a>
        </div>
        <h1 className="h1">Sign In</h1>
        <p className="text_please">Please enter your phone to sign in</p>
        <div>
          <input
            type="text"
            placeholder="Your Phone Number" className="input_phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
        {step === 1 ? (
          <button className="" onClick={sendCode}>Next</button>
        ) : (
          <div>
            <input
              type="text"
              placeholder="Enter Access Code"
              value={code}
              onChange={(e) => setCode(e.target.value)}
            />
            <button onClick={verifyCode}>Verify Code</button>
          </div>
        )}
        <p className="text_passwordless">Passwordless authentication methods</p>
        <p className="text_dont_have_account">
          Don't having account?
          <a href="/signup" className="link_sign_up"> Sign Up</a>
        </p>
      </form>
    </div>
  </div>
  );
}
