import { useState, useEffect } from "react";
import { GoBell } from "react-icons/go";
import { IoPersonCircleOutline } from "react-icons/io5";
import { CiSearch } from "react-icons/ci";
import "./css.css";
import { getStudents, addStudent } from "./../../services/api";

export default function Teacher() {
  const [showModal, setShowModal] = useState(false);
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({
    name: "",
    phone: "",
    email: "",
    role: "",
    address: "",
  });

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const { data } = await getStudents();
      setStudents(data || []);
    } catch (err) {
      console.error("Lỗi khi lấy danh sách:", err);
    }
  };

  const handleChange = (e) =>
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));

  const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    console.log("Submit form:", form); // log dữ liệu gửi đi
    const { name, phone, email, role, address } = form;
    if (!name || !phone || !email) return;
    await addStudent(name, phone, email, role, address);
    console.log("Add student success");
    setShowModal(false);
    setForm({ name: "", phone: "", email: "", role: "", address: "" });
    fetchStudents();
  } catch (err) {
    console.error("Lỗi khi thêm student:", err);
  }
};


  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo"></div>
        <div className="menu">
          <button className="menu-btn active">Manage Students</button>
          <button className="menu-btn">Manage Lessons</button>
          <button className="menu-btn">Message</button>
        </div>
      </div>

      <div className="main">
        <header className="header">
          <a href="/" className="icon-link">
            <GoBell className="icon" />
          </a>
          <a href="/" className="icon-link">
            <IoPersonCircleOutline className="icon" />
          </a>
        </header>

        <section className="content">
          <h2>Manage Students</h2>
          <div className="content-header">
            <div className="actions">
              <p>{students.length} Students</p>
              <button className="add-btn" onClick={() => setShowModal(true)}>
                + Add Student
              </button>
              <div className="filter-input-wrapper">
                <CiSearch className="filter-icon" />
                <input type="text" placeholder="Filter" className="filter-input" />
              </div>
            </div>
          </div>

          <table className="student-table">
            <thead className="thead">
              <tr>
                <th>Student Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th>Role</th>
                <th>Address</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {students.map((s) => (
                <tr key={s.id || `${s.phone}-${s.email}`}>
                  <td>{s.name}</td>
                  <td className="email">{s.email}</td>
                  <td>{s.role || "-"}</td>
                  <td>{s.address || "-"}</td>
                  <td>
                    <button className="edit-btn">Edit</button>
                    <button className="delete-btn">Delete</button>
                  </td>
                </tr>
              ))}
              {students.length === 0 && (
                <tr>
                  <td colSpan={6} style={{ textAlign: "center", padding: 16 }}>
                    Chưa có học viên
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </section>
      </div>

      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2 className="modal-title">Create Student</h2>
            <form className="modal-form" onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label>Student Name</label>
                  <input
                    type="text"
                    name="name"
                    value={form.name}
                    onChange={handleChange}
                  />
                </div>
                
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Email Address</label>
                  <input
                    type="email"
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                  />
                </div>
              
              </div>

              <div className="modal-actions">
                <button type="submit" className="create-btn">
                  Create
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
