import axios from "axios";

const API = axios.create({
  baseURL: "http://localhost:5000", // backend
});

export const createAccessCode = (phoneNumber) =>
  API.post("/createAccessCode", { phoneNumber });

export const validateAccessCode = (phoneNumber, code) =>
  API.post("/validateAccessCode", { phoneNumber, accessCode: code });

export default API;
