import { useState } from "react";
import { createAccessCode, validateAccessCode } from "../../services/api";
import { useNavigate } from "react-router-dom";
import "./login.css";
import { GoArrowLeft } from "react-icons/go";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";

export default function Login() {
  const [phone, setPhone] = useState("");
  const [code, setCode] = useState("");
  const [step, setStep] = useState(1);
  const navigate = useNavigate();

  // Hàm format số Mỹ chuẩn E.164
  const formatUSPhoneNumber = (number) => {
    let cleaned = number.replace(/\D/g, ""); // xóa ký tự không phải số
    if (cleaned.startsWith("1") && cleaned.length === 11) {
      return "+" + cleaned; // đã có mã 1
    } else if (cleaned.length === 10) {
      return "+1" + cleaned; // thêm mã 1
    }
    return "+" + cleaned; // giữ nguyên nếu người dùng đã nhập đầy đủ
  };

  const sendCode = async (e) => {
    e.preventDefault();
    if (!phone) return alert("Please enter phone number");

    const formattedPhone = formatUSPhoneNumber(phone);
    console.log("Formatted Phone:", formattedPhone);
    await createAccessCode(formattedPhone);
    setStep(2);
  };

  const verifyCode = async (e) => {
    e.preventDefault();
    if (!code) return alert("Please enter code");

    const formattedPhone = formatUSPhoneNumber(phone);
    const res = await validateAccessCode(formattedPhone, code);

    const role = res.data.role;
    localStorage.setItem("phone", formattedPhone);
    localStorage.setItem("role", role);

    if (role === "instructor") navigate("/instructor");
    else navigate("/student");
  };

  return (
    <div className="wrapper">
      <div className="logincontainer">
        {step === 1 && (
          <form>
            <div className="back">
              <a href="/" className="back-link">
                <GoArrowLeft className="icon" /> Back
              </a>
            </div>
            <h1 className="h1">Sign In</h1>
            <p className="text_please">Please enter your phone to sign in</p>
            <div>
              <PhoneInput
                country={"us"}
                value={phone}
                onChange={(value) => setPhone(value)}
                inputClass="input_phone"
              />
            </div>
            <button className="button_next" onClick={sendCode}>
              Next
            </button>
            <p className="text_passwordless">
              Passwordless authentication methods
            </p>
            <p className="text_dont_have_account">
              Don't having account?
              <a href="/signup" className="link_sign_up"> Sign Up</a>
            </p>
          </form>
        )}

        {step === 2 && (
          <form>
            <div className="back">
              <a href="/" className="back-link">
                <GoArrowLeft className="icon" /> Back
              </a>
            </div>
            <h1 className="h1">Phone verification</h1>
            <p className="text_please">
              Please enter your code that sent to your phone
            </p>
            <div>
              <input
                type="text"
                placeholder="Enter Your Code"
                className="input_phone"
                value={code}
                onChange={(e) => setCode(e.target.value)}
              />
            </div>
            <button className="button_next" onClick={verifyCode}>
              Submit
            </button>
            <p className="text_dont_have_account">
              Code not receive?
              <a href="#" onClick={sendCode} className="link_sign_up">
                {" "}Send again
              </a>
            </p>
          </form>
        )}
      </div>
    </div>
  );
}
